mapper.insert:

	SELECT PAYANG_SEQ.NEXTVAL FROM DUAL 


INSERT INTO missing (
					 DOC_NO
					,DOC_EMAIL
					,DOC_NICK
					,DOC_NAME
					,DOC_GENDER
					,DOC_BIG_NAME
					,DOC_KIND
					,DOC_AGE
					,DOC_SIDO
					,DOC_SIGUNGU
					,DOC_ADDRESS
					,DOC_SUBJECT
					,DOC_CONTENT
					,DOC_DATE
					,DOC_UPD_DATE
					)
			VALUES (
					#{doc_no}
					,#{doc_email}
					,#{doc_nick}
					,#{doc_name}
					,#{doc_gender}
					,#{doc_big_name}
					,#{doc_kind}
					,#{doc_age}
					,#{doc_sido}
					,#{doc_sigungu}
					,#{doc_address}
					,#{doc_subject}
					,#{doc_content}
					,sysdate
					,sysdate
					)

mapper.update:
UPDATE missing
SET	 doc_subject = #{doc_subject}
	,doc_name = #{doc_name}
	,doc_gender = #{doc_gender}
	,doc_big_name = #{doc_big_name}
	,doc_kind = #{doc_kind}
	,doc_age = #{doc_age}
	,doc_sido = #{doc_sido}
	,doc_sigungu = #{doc_sigungu}
	,doc_address = #{doc_address}
	,doc_content = #{doc_content}
WHERE doc_no = #{doc_no}

mapper.insert:

	SELECT PAYANG_REPLY_SEQ.NEXTVAL FROM DUAL 

INSERT INTO missing_reply (
						 DOC_CNO
						,DOC_NO
						,DOC_EMAIL
						,DOC_NICK
						,DOC_CONTENT
						,DOC_SECRET
						,DOC_DATE
						,DOC_UPD_DATE
						)
						VALUES
						(
							#{doc_cno}
							,#{doc_no}
							,#{doc_email}
							,#{doc_nick}
							,#{doc_content}
							,#{doc_secret}
							, SYSDATE
							, SYSDATE
						)


	SELECT NVL(doc_readCount,0) + 1 FROM missing WHERE doc_no = #{doc_no}

UPDATE missing
SET doc_readCount = #{doc_readcount}
WHERE doc_no = #{doc_no}

mapper.update:
UPDATE missing_reply
SET doc_content = #{doc_content}
WHERE doc_cno = #{doc_cno}
