mapper.insert:
		 
			SELECT PAYANG_SEQ.NEXTVAL FROM DUAL 
		

		INSERT INTO payang (
							 DOC_NO
							,DOC_EMAIL
							,DOC_NICK
							,DOC_NAME
							,DOC_GENDER
							,DOC_BIG_NAME
							,DOC_KIND
							,DOC_AGE
							,DOC_SIDO
							,DOC_SIGUNGU
							,DOC_ADDRESS
							,DOC_SUBJECT
							,DOC_CONTENT
							,DOC_DATE
							,DOC_UPD_DATE
							)
					VALUES (
							#{doc_no}
							,#{doc_email}
							,#{doc_nick}
							,#{doc_name}
							,#{doc_gender}
							,#{doc_big_name}
							,#{doc_kind}
							,#{doc_age}
							,#{doc_sido}
							,#{doc_sigungu}
							,#{doc_address}
							,#{doc_subject}
							,#{doc_content}
							,sysdate
							,sysdate
							)

mapper.update:
UPDATE payang
SET	 doc_subject = #{doc_subject}
	,doc_name = #{doc_name}
	,doc_gender = #{doc_gender}
	,doc_big_name = #{doc_big_name}
	,doc_kind = #{doc_kind}
	,doc_age = #{doc_age}
	,doc_sido = #{doc_sido}
	,doc_sigungu = #{doc_sigungu}
	,doc_address = #{doc_address}
	,doc_content = #{doc_content}
WHERE doc_no = #{doc_no}

mapper.insert:
 
