#{doc_search}

or doc_loc in

#{doc_search}

 order by doc_no desc)) where rnum>=#{startrow} and rnum<=#{endrow}

 select count(*) from adopt_list where doc_kindof in
 
 #{kind_search}
 
 or doc_loc in
 
 #{kind_loc}
 
