mapper.insert:
 
	SELECT PAYANG_REPLY_SEQ.NEXTVAL FROM DUAL 

INSERT INTO payang_reply (
						 DOC_CNO
						,DOC_NO
						,DOC_EMAIL
						,DOC_NICK
						,DOC_CONTENT
						,DOC_SECRET
						,DOC_DATE
						,DOC_REF
						,DOC_LEV
						,DOC_SEQ
						)
						VALUES
						(
							#{doc_cno}
							,#{doc_no}
							,#{doc_email}
							,#{doc_nick}
							,#{doc_content}
							,#{doc_secret}
							, SYSDATE
							,#{doc_ref}
							,(select doc_lev+1 from payang_reply WHERE doc_cno = #{doc_ref})
							,(select nvl(max(doc_seq),0) +1 from payang_reply WHERE doc_ref = #{doc_ref})
						)
